import React, { useEffect, useState } from "react";
import styles from './TelaCurso.module.css';
import BarraDeNavegacao from "../../Components/BarraDeNavegacao/BarraDeNavegacao";
import Mentor from '../../Components/Mentor/Mentor';
import axios from "axios";

function TelaCurso({ nomedocurso, oferece, idAula }) {

    const [descricao, setDescricao] = useState([]);
    const [descricaoFiltrada, setDescricaoFiltrada] = useState(null);

    useEffect(() => {
        async function BuscarDescricao() {
            try {
                const response = await axios.get("https://localhost:44333/api/athena/listaraulas");
                console.log('Dados da API:', response.data); // Verifique os dados recebidos da API
                setDescricao(response.data);
                const idNumber = parseInt(idAula, 10); // Converter o ID para número, se necessário
                const descricaoEncontrada = response.data.find(item => item.id === idNumber);
                setDescricaoFiltrada(descricaoEncontrada);
            } catch (error) {
                console.error("Erro ao buscar descrição:", error);
            }
        }

        BuscarDescricao();

        console.log('ID da Aula:', idAula); // Verifique se o ID da aula está sendo passado corretamente

    }, [idAula])

    return (
        <div className={styles.container}>
            <BarraDeNavegacao></BarraDeNavegacao>
            <div className={styles.up}>
                {descricaoFiltrada ? (
                    <>
                        <div className={styles.descricao}>
                            <span className={styles.descA}>Descrição</span>
                            <span className={styles.descB}>{descricaoFiltrada.desc}</span>
                        </div>
                        <div className={styles.containervideo}>
                            <video></video>
                            <div>
                                <div>
                                    <span>{nomedocurso}</span>
                                </div>
                                <Mentor></Mentor>
                            </div>
                        </div>
                    </>
                ) : (
                    <span>Descrição não encontrada</span>
                )}
            </div>

            <div className={styles.containerbaixo}>
                <div className={styles.oferecimentos}>
                    <span>Este curso oferece:</span>
                    <span>{oferece}</span>
                </div>
            </div>
            <div className={styles.comentarios}>
                <span>Comentários</span>
                <input type='text' name='Comentarios' placeholder='Adicione seu comentário! ...'></input>
            </div>
        </div>
    )
}

export default TelaCurso;
